import React, {useState, useEffect} from "react";
import {Col, Container, Row} from "react-bootstrap";
import Message from "../components/Message";
import {Link} from "react-router-dom";
import Provider from "../components/Provider";

function ProviderScreen({match, history}) {

    const categoryList = [
        {
            "id": "1",
            "name": "Mobile",
            "providers": [
                {
                    "id": "1",
                    "name": "Bakcell",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "2",
                    "name": "Nar",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "3",
                    "name": "Azercell",
                    "fields": [
                        {}
                    ]
                }
            ]
        },
        {
            "id": "2",
            "name": "Utilities",
            "providers": [
                {
                    "id": "1",
                    "name": "Azeriqaz",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "2",
                    "name": "Azerisu",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "3",
                    "name": "Grand Hayat",
                    "fields": [
                        {}
                    ]
                }
            ]
        },
        {
            "id": "3",
            "name": "Banks",
            "providers": [
                {
                    "id": "1",
                    "name": "Kapital Bank",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "1",
                    "name": "Pasha Bank",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "1",
                    "name": "Access Bank",
                    "fields": [
                        {}
                    ]
                }
            ]
        }
    ]

    const categoryId = match.params.id;

    const [category, setCategory] = useState();
    const [providers, setProviders] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        try {
            const categoryFiltered = categoryList.filter(category => category.id === categoryId);
            setCategory(categoryFiltered);
            setProviders(category);
        } catch (error) {
            console.error(error);
            setError(error);
        } finally {
            setLoading(false);
        }

    }, []);

    // const addToCartHandler = () => {
    //     history.push(`/cart/${match.params.id}?qty=${qty}`);
    // };
    //
    // const submitHandler = (e) => {
    //     e.preventDefault();
    //     dispatch(
    //         createProductReview(match.params.id, {
    //             rating,
    //             comment,
    //         })
    //     );
    // };


    if (loading) return "Loading...";
    if (error) return <Message variant="danger">{error}</Message>

    return (
        <Container>
            <h1>Providers</h1>
            <Link to="/" className="btn btn-light my-3">
                Go Back
            </Link>
            <div>
                <Row>
                    {providers.map((provider) => (
                        <Col key={provider.id} sm={12} md={6} lg={4} xl={3}>
                            <Provider provider={provider}/>
                        </Col>
                    ))}
                </Row>
            </div>
        </Container>
    );
}

export default ProviderScreen;
