    return (
        <Container>
            <h1>Categories</h1>
            <div>
                <Row>
                    {categories.map((category) => (
                        <Col key={category.id} sm={12} md={6} lg={4} xl={3}>
                            <Category category={category}/>
                        </Col>
                    ))}
                </Row>
            </div>
        </Container>
    );
}

