                },
                {
                    "id": "1",
                    "name": "Pasha Bank",
                    "fields": [
                        {}
                    ]
                },
                {
                    "id": "1",
                    "name": "Access Bank",
                    "fields": [
                        {}
                    ]
                }
            ]
        }
    ]

    const [categories, setCategories] = useState();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // useEffect(() => {
    //     fetch("testURL")
