import React, {useState, useEffect} from "react";
import {Col, Container, Form, Row, Button, Card} from "react-bootstrap";
import Message from "../components/Message";
import {Link} from "react-router-dom";

function FormScreen({match, history}) {

    const categoryList = [
        {
            "id": "1",
            "name": "Mobile",
            "providers": [
                {
                    "id": "1",
                    "name": "Bakcell",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "055"},
                            ]
                        },
                        {
                            "id": "phone_number",
                            "type": 2,
                            "label": "Phone number",
                        }
                    ]
                },
                {
                    "id": "2",
                    "name": "Nar",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "055"},
                                {"k": "2", "v": "077"},
                            ]
                        }
                    ]
                },
                {
                    "id": "3",
                    "name": "Azercell",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "050"},
                                {"k": "2", "v": "051"},
                            ]
                        }
                    ]
                }
            ]
        },
        {
            "id": "2",
            "name": "Utilities",
            "providers": [
                {
                    "id": "1",
                    "name": "Azeriqaz",
                    "fields": [
                        {
                            "id": "subscriber_id",
                            "type": 1,
                            "label": "Subscriber"
                        }
                    ]
                },
                {
                    "id": "2",
                    "name": "Azerisu",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "Residential"},
                                {"k": "2", "v": "Commercial"},
                            ]
                        },
                        {
                            "id": "subscriber_id",
                            "type": 1,
                            "label": "Subscriber"
                        }
                    ]
                },
                {
                    "id": "3",
                    "name": "Grand Hayat",
                    "fields": [
                        {
                            "id": "client_code",
                            "type": 2,
                            "label": "Client code"
                        }
                    ]
                }
            ]
        },
        // {
        //     "id": "3",
        //     "name": "Banks",
        //     "providers": [
        //         {
        //             "id": "1",
        //             "name": "Kapital Bank",
        //             "fields": [
        //                 {}
        //             ]
        //         },
        //         {
        //             "id": "1",
        //             "name": "Pasha Bank",
        //             "fields": [
        //                 {}
        //             ]
        //         },
        //         {
        //             "id": "1",
        //             "name": "Access Bank",
        //             "fields": [
        //                 {}
        //             ]
        //         }
        //     ]
        // }
    ]

    const categoryId = match.params.categoryId;
    const providerId = match.params.providerId;

    const [type, setType] = useState(null);
    const [field, setField] = useState();
    const [providers, setProviders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // form
    const [number, setNumber] = useState('');
    const [exp_month, setExpMonth] = useState('');
    const [exp_year, setExpYear] = useState('');
    const [cvv, setCvv] = useState('');

    const [value, setValue] = useState('');
    const [currency, setCurrency] = useState('');

    const [option, setOption] = useState('');
    const [input, setInput] = useState('');

    useEffect(() => {
        try {

            const categoryFiltered = categoryList.filter(category => category.id === categoryId);
            const providerFiltered = providers.filter(provider => provider.id === providerId);

            setProviders(categoryFiltered[0].providers);
            setField(providerFiltered[0]);

        } catch (error) {
            console.error(error);
            setError(error);
        } finally {
            setLoading(false);
        }
    }, [categoryId, providerId, loading, error]);

    console.log(field)

    const [validated, setValidated] = useState(false);

    const handleSubmit = (event) => {

        const form = event.currentTarget;
        console.log(form)
        if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
        }
        event.preventDefault();
        setValidated(true);

        console.log(
            number,
            exp_month,
            exp_year,
            cvv,

            value,
            currency,

            option,
            input
        )

        history.push(`/success`);
    };


    if (loading) return "Loading...";
    if (error) return <Message variant="danger">{error}</Message>

    return (
        <Container>
            <h1>Form</h1>
            <Link to="/" className="btn btn-light my-3">
                Go Back
            </Link>
            <div>
                <Form noValidate validated={validated} onSubmit={handleSubmit}>
                    <Row>
                        <Col sm={12} md={6} lg={4} xl={4}>
                            <div className="card-container">
                                {field && field.fields.map((f) => (
                                    <div key={f.if}>
                                        <Form.Group className="mb-2" controlId="formBasicEmail">
                                            <Form.Label>{f.label}</Form.Label>
                                            {
                                                f.type === 4 ?
                                                    (
                                                        <Form.Control
                                                            name={f.id}
                                                            as="select"
                                                            required
                                                            onChange={(e) => setOption(e.target.value)}
                                                        >
                                                            <option value="">Select...</option>
                                                            {
                                                                f.options.map((option, index) => (
                                                                    <option key={index}
                                                                            value={option.v}>{option.v}</option>
                                                                ))
                                                            }
                                                        </Form.Control>
                                                    )
                                                    : (
                                                        <Form.Control
                                                            required
                                                            name={f.id}
                                                            type={f.type === 1 ? 'text' : f.type === 2 || f.type === 3 ? 'number' : f.type === 5 ? 'date' : 'text'}
                                                            placeholder={f.label}
                                                            onChange={(e) => setInput(e.target.value)}
                                                        />
                                                    )
                                            }
                                            <Form.Text className="text-muted">
                                                We'll never share your email with anyone else.
                                            </Form.Text>
                                        </Form.Group>
                                    </div>
                                ))}
                                <Form.Group className="mb-2" controlId="value">
                                    <Form.Label>Amount</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Amount"
                                        required
                                        value={value}
                                        onChange={(e) => setValue(e.target.value)}
                                    />
                                </Form.Group>
                                <Form.Group className="mb-2" controlId="currency">
                                    <Form.Label>Currency</Form.Label>
                                    <Form.Control
                                        as="select"
                                        required
                                        value={currency}
                                        onChange={(e) => setCurrency(e.target.value)}
                                    >
                                        <option value="">Select...</option>
                                        <option value="USD">USD</option>
                                    </Form.Control>
                                </Form.Group>
                            </div>
                        </Col>
                        <Col sm={12} md={6} lg={8} xl={5}>
                            <div className="card-container">
                                <Col sm={12} md={12} lg={12} xl={12}>
                                    <Form.Group className="mb-2" controlId="number">
                                        <Form.Label>Card number</Form.Label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Card number"
                                            required
                                            className="cardNumber"
                                            value={number}
                                            onChange={(e) => setNumber(e.target.value)}
                                        />
                                    </Form.Group>
                                </Col>
                                <Col sm={12} md={12} lg={12} xl={12}>
                                    <Row>
                                        <Col sm={12} md={8} lg={6} xl={6}>
                                            <Form.Label>Exp date</Form.Label>
                                            <Form.Group className="exp mb-2" controlId="exp_month">
                                                <Form.Control
                                                    type="text"
                                                    placeholder="ExpMonth"
                                                    required
                                                    className="exp-left"
                                                    value={exp_month}
                                                    onChange={(e) => setExpMonth(e.target.value)}
                                                />
                                                <span className="exp-line"/>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="ExpYear"
                                                    required
                                                    className="exp-right"
                                                    value={exp_year}
                                                    onChange={(e) => setExpYear(e.target.value)}
                                                />
                                            </Form.Group>
                                        </Col>
                                        <Col sm={12} md={8} lg={6} xl={6}>
                                            <Form.Group className="mb-2" controlId="cvv">
                                                <Form.Label>CVV</Form.Label>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="CVV"
                                                    required
                                                    className="cvv"
                                                    value={cvv}
                                                    onChange={(e) => setCvv(e.target.value)}
                                                />
                                            </Form.Group>
                                        </Col>
                                    </Row>
                                </Col>
                                <Col sm={12} md={12} lg={12} xl={12}>
                                    <Button className='submitButton' variant="primary" type="submit">
                                        Submit
                                    </Button>
                                </Col>
                            </div>
                        </Col>
                    </Row>
                </Form>
            </div>
        </Container>
    );
}

export default FormScreen;
