import React, {useState, useEffect} from "react";
import {Col, Container, Form, Row, Button} from "react-bootstrap";
import Message from "../components/Message";
import {Link} from "react-router-dom";

function FormScreen({match, history}) {

    const categoryList = [
        {
            "id": "1",
            "name": "Mobile",
            "providers": [
                {
                    "id": "1",
                    "name": "Bakcell",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "055"},
                            ]
                        },
                        {
                            "id": "phone_number",
                            "type": 2,
                            "label": "Phone number",
                        }
                    ]
                },
                {
                    "id": "2",
                    "name": "Nar",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "055"},
                                {"k": "2", "v": "077"},
                            ]
                        }
                    ]
                },
                {
                    "id": "3",
                    "name": "Azercell",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "050"},
                                {"k": "2", "v": "051"},
                            ]
                        }
                    ]
                }
            ]
        },
        {
            "id": "2",
            "name": "Utilities",
            "providers": [
                {
                    "id": "1",
                    "name": "Azeriqaz",
                    "fields": [
                        {
                            "id": "subscriber_id",
                            "type": 1,
                            "label": "Subscriber"
                        }
                    ]
                },
                {
                    "id": "2",
                    "name": "Azerisu",
                    "fields": [
                        {
                            "id": "prefix_id",
                            "type": 4,
                            "label": "Prefix",
                            "options": [
                                {"k": "1", "v": "Residential"},
                                {"k": "2", "v": "Commercial"},
                            ]
                        },
                        {
                            "id": "subscriber_id",
                            "type": 1,
                            "label": "Subscriber"
                        }
                    ]
                },
                {
                    "id": "3",
                    "name": "Grand Hayat",
                    "fields": [
                        {
                            "id": "client_code",
                            "type": 2,
                            "label": "Client code"
                        }
                    ]
                }
            ]
        },
        // {
        //     "id": "3",
        //     "name": "Banks",
        //     "providers": [
        //         {
        //             "id": "1",
        //             "name": "Kapital Bank",
        //             "fields": [
        //                 {}
        //             ]
        //         },
        //         {
        //             "id": "1",
        //             "name": "Pasha Bank",
        //             "fields": [
        //                 {}
        //             ]
        //         },
        //         {
        //             "id": "1",
        //             "name": "Access Bank",
        //             "fields": [
        //                 {}
        //             ]
        //         }
        //     ]
        // }
    ]

    const categoryId = match.params.categoryId;
    const providerId = match.params.providerId;

    const [type, setType] = useState(null);
    const [field, setField] = useState();
    const [providers, setProviders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        try {

            const categoryFiltered = categoryList.filter(category => category.id === categoryId);
            const providerFiltered = providers.filter(provider => provider.id === providerId);

            setProviders(categoryFiltered[0].providers);
            setField(providerFiltered[0]);

        } catch (error) {
            console.error(error);
            setError(error);
        } finally {
            setLoading(false);
        }
    }, [categoryId, providerId, loading, error]);

    console.log("categoryFiltered", providers);
    console.log("fields", field);

    // const addToCartHandler = () => {
    //     history.push(`/cart/${match.params.id}?qty=${qty}`);
    // };
    //
    // const submitHandler = (e) => {
    //     e.preventDefault();
    //     dispatch(
    //         createProductReview(match.params.id, {
    //             rating,
    //             comment,
    //         })
    //     );
    // };


    if (loading) return "Loading...";
    if (error) return <Message variant="danger">{error}</Message>

    return (
        <Container>
            <h1>Form</h1>
            <Link to="/" className="btn btn-light my-3">
                Go Back
            </Link>
            <div>
                <Row>
                    <Form>
                        <Col sm={12} md={6} lg={4} xl={3}>
                        {field && field.fields.map((f) => (

                                <Form.Group className="mb-3" controlId="formBasicEmail">
                                    <Form.Label>{f.label}</Form.Label>
                                    {
                                        f.type === 4 ?
                                            (
                                                // <Form.Select defaultValue="Choose...">
                                                //     <option>Choose...</option>
                                                //     <option>...</option>
                                                // </Form.Select>
                                                <Form.Control
                                                    as="select"
                                                    required
                                                    // value={f.}
                                                    // onChange={(e) => setRating(e.target.value)}
                                                >
                                                    <option value="">Select...</option>
                                                    <option value="1">1 - Poor</option>
                                                </Form.Control>
                                            )
                                            : (
                                                <Form.Control
                                                    required
                                                    type={f.type === 1 ? 'text' : f.type === 2 || f.type === 3 ? 'number' : f.type === 5 ? 'date' : 'text'}
                                                    placeholder={f.label}
                                                />
                                            )
                                    }
                                    <Form.Text className="text-muted">
                                        We'll never share your email with anyone else.
                                    </Form.Text>
                                </Form.Group>
                        ))}
                        <Col className="mb-2" sm={12} md={6} lg={4} xl={3}>
                            <Form.Group controlId="value">
                                <Form.Label>Amount</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Amount"
                                    // value={value}
                                    // onChange={(e) => setValue(e.target.value)}
                                />
                            </Form.Group>
                        </Col>
                        <Col className="mb-2" sm={12} md={6} lg={4} xl={3}>
                            <Form.Group controlId="currency">
                                <Form.Label>Currency</Form.Label>
                                <Form.Control
                                    as="select"
                                    required
                                    // value={currency}
                                    // onChange={(e) => setCurrency(e.target.value)}
                                >
                                    <option value="">Select...</option>
                                    <option value="USD">USD</option>
                                </Form.Control>
                            </Form.Group>
                        </Col>
                        </Col>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </Form>
                </Row>
            </div>
        </Container>
    );
}

export default FormScreen;
